import React, {useState} from 'react';

const Questions = () => {
    const [questions, setQuestions] = useState([
        {id:1, question: 'Capital of GB?', answer: 'London'},
        {id:2, question: 'Capital of Ukraine?', answer: 'Kyiv'},
        {id:3, question: 'Capital of Turkey?', answer: 'Ancara'},
        {id:4, question: 'Capital of Saqartvelos?', answer: 'Tbilisi'},
        {id:5, question: 'Capital of GB?', answer: 'London'},
    ])

    const [randomNumber, setRandomNumber] = useState(0)

    let min;
    let max;
    const mapHandler = (item) => {
        questions.forEach((i)=>{
            return min = i.item[0],
            max = i.item[questions.length-1]
        })

    }
    mapHandler('id')

    function getRandomIntInclusive(min, max) {

        min = Math.ceil(min);
        max = Math.floor(max);
        return setRandomNumber(Math.floor(Math.random() * (max - min + 1)) + min); //Максимум и минимум включаются
    }

    return (
        <div>
            <p>{randomNumber}</p>
            <button>check</button>
            <button onClick={()=>getRandomIntInclusive()}>change</button>
        </div>
    );
};

export default Questions;
