import React from 'react';
import {useState} from "react";
import Carousel from "./Carousel";

const Test = () => {

    const arr = []
    const [tests, setTests] = useState([
        {id:1, question: 'Capital of Poland?', answer: 'Warshaw'},
        {id:2, question: 'Capital of Ukraine?', answer: 'Kyiv'},
        {id:3, question: 'Capital of Turkey?', answer: 'Ankara'},
        {id:4, question: 'Capital of Saqartvelos?', answer: 'Tbilisi'},
        {id:5, question: 'Capital of Great Britain?', answer: 'London'},
    ])
    const [checkAnswer, setCheckAnswer] = useState('');

    const checkTest = () => {
        if (tests.find( item => item.answer === checkAnswer) !== undefined) arr.push( `${checkAnswer} is OK`)
        else arr.push( `${checkAnswer} is NOT OK`)
    }

    const showResultHandler = () => {
      return alert(arr)
    }

    return (
            <div>

                <Carousel>

                    <div className='item item-1'>
                        <p>Capital of Poland?</p>
                        <input  onChange={e=>setCheckAnswer(e.target.value)}
                                value={checkAnswer}/>
                        <button onClick={checkTest}>check</button>
                    </div>

                    <div className='item item-2'>
                        <p>Capital of Ukraine?</p>
                        <input  onChange={e=>setCheckAnswer(e.target.value)}
                                value={checkAnswer}/>
                        <button onClick={checkTest}>check</button>
                    </div>
                    <div className='item item-3'>
                        <p>Capital of Turkey?</p>
                        <input onChange={e=>setCheckAnswer(e.target.value)}
                               value={checkAnswer}/>
                        <button onClick={checkTest}>check</button>
                    </div>
                    <div className='item item-1'>
                        <p>Capital of Saqartvelos?</p>
                        <input  onChange={e=>setCheckAnswer(e.target.value)}
                                value={checkAnswer}/>
                        <button onClick={checkTest}>check</button>
                    </div>
                    <div className='item item-2'>
                        <p>Capital of Great Britain?</p>
                        <input  onChange={e=>setCheckAnswer(e.target.value)}
                                value={checkAnswer}/>
                        <button onClick={checkTest}>check</button>
                    </div>
                </Carousel>
            <button onClick={showResultHandler}>SHOW RESULT</button>
            </div>
    );
};

export default Test;
