import React from 'react';
import {useState} from "react";

const AddTarget = ({add}) => {

    const [worker, setWorker] = useState({name: '',surname: ''});


    const addHandler = (e) => {

            e.preventDefault();

            const newItem = {...worker, id: Date.now()}
        setWorker({name: '',surname: ''})

            add(newItem)

    }

    return (
        <div>
            <p key={Date.now()}> {worker}</p>
            <input
                className='inputArea'
                type="text"
                value={worker.title}
                onChange={event=>setWorker({...worker, name: event.target.value})}
                placeholder={'What you need to do'}
            />
            <input
                className='descriptionArea'
                type="text"
                value={worker.description}
                onChange={event=>setWorker({...worker, surname: event.target.value})}
                placeholder={'description'}
            />


            <button onClick={addHandler}>add</button>
        </div>
    );
};

export default AddTarget;
